// The Vue build version to load with the `import` command
// (runtime-only or standalone) has been set in webpack.base.conf with an alias.
import Vue from 'vue'
import App from './App'
import router from './router'
import ElementUI from 'element-ui'
import 'element-ui/lib/theme-chalk/index.css'

Vue.use(ElementUI)
Vue.directive('img', function (el, binding) {
  // 简单压缩图片
  el.width = 200
  // 设置手型
  el.style.cursor = 'url(data:image/svg+xml;base64,), auto'
  // 创建一张大图
  let img = new window.Image()
  // 设置大图图片地址和小图一样
  img.src = el.src
  // 设置大图图片css样式，在页面垂直居中
  img.style.position = 'fixed'
  img.style.maxWidth = img.style.maxHeight = '90%'
  img.style.top = 0
  img.style.right = 0
  img.style.bottom = 0
  img.style.left = 0
  img.style.margin = 'auto'
  img.style.zIndex = 10086
  // 设置大图手型
  img.style.cursor = 'url(data:image/svg+xml;base64,), auto'
  // 侦听大图点击事件，点击移除大图
  img.addEventListener('click', () => {
    document.body.removeChild(img)
  })
  // 侦听小图点击事件，点击插入大图
  el.addEventListener('click', () => {
    document.body.appendChild(img)
  })
})

Vue.config.productionTip = false

/* eslint-disable no-new */
new Vue({
  el: '#app',
  router,
  components: { App },
  template: '<App/>'
})
